"""# How does the random seed affect the output?"""

def random_interpolate(image_class, scale, random_seed):
  with tf.Session() as sess:
    images = None

    #initialize the variables
    sess.run(tf.global_variables_initializer())
    
    #truncation value 
    trunc = 1.0
    
    index = image_class
  
          
    # sample a vector with the same seed 
    z, y = get_zy(index, seed = random_seed)
  
    #generate the images with different scaling factor 
    im1 = generate(sess, -1.0*(z/np.abs(z)), y)
    im2 = generate(sess, -scale*(z/np.abs(z)), y)
  
  
    images = np.concatenate([im2, im1], axis = 1)
    #show the image 
    plt.axis('off')
